function handlerFeedback(url, feedback) {
      let event = {
        name: 'interstitials_feedback_btn',
        payload: {
          url: url,
      	  feedback: feedback
        }
      }
      try {
        const targetOrigin = "*";
		window.parent.postMessage(JSON.stringify(event), targetOrigin);
      } catch(err) {console.log("Irame error" , err)}

      try {
       WebViewInterface.sendInterstitialFeedback(JSON.stringify(event)); //Android
      } catch(err) {}

      try {
       window.webkit.messageHandlers.interstitials.postMessage(JSON.stringify(event)); //IOS
      } catch(err) {}
}
function sendSize() {
      setTimeout(() => {

        let name = 'root container responsivegrid'
        let errorName = 'style_page_5'
        let content = document.getElementsByClassName(name)[0]
        let errorContent = document.getElementsByClassName(errorName)[0]

        if(!errorContent && content) {
          const height = content.getBoundingClientRect().height

          let event = {
            name: 'interstitial_resize',
            payload: {
              height: height
            }
          }

          try {
            const targetOrigin = "*";
            window.parent.postMessage(JSON.stringify(event), targetOrigin);
          } catch(err) {console.log("Irame error on resize" , err)}

        }
      }, 100)
    }
window.addEventListener('resize', sendSize)